import mainContext from '../context/mainContext'


const AllMoviesPage = () => {

    const {socket, getMovieList, setMovieList } = useContext(mainContext) 


    const seatList = []
    for (let i = 0; i <= 30; i++) {
            seatList.push({
                seatNumber: i +1,
                reserved: "",
                price: 7.99
            })
        }

    const movies = [
        { title: "Mulholland Drive",
        year: "2001",
        census: 16,
        genre: "Psychological thriller",
        seats: seatList   
    },
        { title: "Up",
        year: "2009",
        census: 0,
        genre: "Animation",
        seats: seatList   
    },
        { title: "Chungking Express",
        year: "1994",
        census: 12,
        genre: "Drama",
        seats: seatList   
    },
    ]

    setMovieList(movies)



  return (
    <div>AllMoviesPage</div>
  )
}

export default AllMoviesPage
