import { createSlice } from "@reduxjs/toolkit";
import data from "../assets/furniture"

export const itemsSlice = createSlice({
    name: "items",
    initialState: {
        value: {
            allProducts: data,
            categories: [],
            colors: [],
            priceRange: [],
            randomItems: []

        }

    },
    reducers: {
        // getallItems: (state, action) => {
        //     state.value.allProducts = [...action.payload]
        //     console.log(state.value.allProducts)
        // },
        // homePageItems: (state, action) => {

        // },
        randomItems: (state, action) => {
            const currentItem = action.payload
            const newArr = [...state.value.allProducts]

            state.value.randomItems = newArr.filter(x => x.category === currentItem.category &&  x.id !== currentItem.id).sort((a, b) => 0.5 - Math.random()).splice(0, 5)

        }

    }




})

export const {getallItems, randomItems} = itemsSlice.actions


export default itemsSlice.reducer
