import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import SingleItemCart from '../components/SingleItemCart'
import { deletefromChart } from '../features/chart'
import {Navbar, Nav, Container, Button, Table} from 'react-bootstrap'


const ShoppingCartPage = () => {
const items = useSelector(state => state.chart.value.chartItems)  
const dispatch = useDispatch() 
console.log(items)

function deleteItem(index){

    const deleteditem = {
        item: items[index],
        array: [...items].filter((x, i) => i !== index)
    }



dispatch(deletefromChart(deleteditem))

}

  return (
    <>
    {/* <Container className='w-80 d-flex flex-column gap-2'>
           {items.map((x, i ) => <SingleItemCart item={x} deleteItem={() => deleteItem(i)} />)}
    </Container> */}

       <Table striped bordered hover className='d-flex flex-column'>
      <thead className='w-100'>
        <tr>
          <th>Product</th>
          <th></th>
          <th>Quantity</th>
          <th>Single</th>
          <th>Total</th>
        </tr>
      </thead>
      <tbody className='d-flex'>
        {items.map((x, i ) => <SingleItemCart item={x} deleteItem={() => deleteItem(i)} />)}

      </tbody>
    </Table>

</>
  )
}

export default ShoppingCartPage
