import React, {useEffect, useState, useContext} from 'react'
import mainContext from '../context/mainContext'

const SingleBox = ({singleday, clickOnDay, setClicked}) => {

  const {socket} = useContext(mainContext)

    const [leftappt, setleftappt] = useState(8)

    useEffect(()=> {

        let apps = singleday.hours.filter(x => x.reservedBy === "").length
        setleftappt(apps)

         socket.on("singleday", (day) => {
            setClicked(day)  
        }) 

    },[singleday])


  return (
    <div onClick={clickOnDay}  className="singlebox"> 
    <h3>Day: {singleday.day}</h3>
    <p>Left appts: {leftappt}</p>
    </div>

  )
}

export default SingleBox
