import React, {useState, useContext, useEffect} from 'react'
import EnemyBox from '../components/EnemyBox'
import SingleBox from '../components/SingleBox'
import mainContext from '../context/mainContext'


const GamePage = () => {


const {socket, playerID, enemyID} = useContext(mainContext) 
const [playersTurn, setPlayersTurn] = useState(true)
// const [enemysTurn, setEnemysTurn] = useState(false)
const [showBtn, setShowBtn] = useState(false)


const makeDamage = async () => {
  const randomDamage = Math.floor(Math.random() * (10 - 0) + 0)
  await  socket.emit("reduce_points", randomDamage)
  if(playerID === socket.id) {
    setPlayersTurn(false)

  } else if(enemyID === socket.id) {
    setPlayersTurn(true)

  }



}

useEffect(() => {
  if((playersTurn && socket.id === playerID )|| (!playersTurn  && socket.id === enemyID)) {
    setShowBtn(true)
  }  else {
    setShowBtn(false)
  }

  console.log(`Player shoud go: ${playersTurn && socket.id === playerID}`)
  console.log(`Enemy should go: ${!playersTurn  && socket.id === enemyID}`)


  // console.log(firstTurn === socket.id && playersTurn === true)
  // console.log(firstTurn !== socket.id && playersTurn === false)

}, [socket])





  return (
    <div className='gameContainer'>
      <SingleBox/>
      { showBtn  ? 
      (
         <button onClick={makeDamage}>attack</button>
      ) 
      :
      "Enemy's turn"
        }


      <EnemyBox/>

    </div>
  )
}

export default GamePage
