import {React, useEffect, useState} from 'react'
import {useSelector, useDispatch} from "react-redux"
import SingleItem from '../components/SingleItem'
import { Container, Row, Button, Dropdown} from 'react-bootstrap'

import {useNavigate} from "react-router-dom"
import data from "../assets/furniture"
import FilterItems from '../components/FilterItems'



function ShopPage({setsingleitem, items}) {

  const [show, setShow] = useState(false);

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);   

  const nav = useNavigate()

let colorarr = []
  const newarrr = [...items]

  // const existingItem = state.value.chartItems.find((item) => item.id === newItem.id)

  //           if(existingItem){
  //               existingItem.quantity++
  //               existingItem.totalPrice += newItem.price
  //               state.value.quantity++
  //               state.value.totalPrice += newItem.price
  //           }

  //           else {
  //               state.value.chartItems.push(action.payload) 
  //               state.value.quantity++
  //               state.value.totalPrice += newItem.price
  //           }

  newarrr.map(x => {
    const singlecolor = {
      color: x.color,
      quantity: 1
    }
    const colorExists = colorarr.find(item => item.color === x.color)
    if(colorExists){
      colorExists.quantity++
    } else {
      colorarr.push(singlecolor)
    }

  })
console.log(colorarr)



function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}





  return (

    <Container className='d-flex flex-column m-auto py-5'>
      <Container className='d-flex gap-2'>
        <Button className='w-25' variant="secondary" onClick={handleShow}>
        Filter products
      </Button >

      <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Sort by:
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">Price low to high</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Price high to low</Dropdown.Item>
        <Dropdown.Item href="#/action-3">Biggest discount</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>

      </Container>

       <Container className='d-flex justify-content-center align-items-center m-auto py-5'>
          <Row xs={1} md={2} lg={4} className=" bg-succes" >  
          {items.map((x, i) => <SingleItem  item={x} key={i} openItem={() => openItem(x.id, x)}  /> )}
       </Row>
       </Container>

       <FilterItems handleClose={handleClose} show={show}/>
    </Container>
  )
}

export default ShopPage
