  await  socket.emit("who_should_go", socket.id) 
  setFirstMove(false)
}

console.log(nextTurn)

useEffect(()=> {

if(nextTurn){
  if(nextTurn === socket.id){
  setShowBtn(true)
} else {
  setShowBtn(false)
}
} else {

  console.log("something")
}


