import React, { useEffect, useState, useContext } from 'react'
import SingleBox from '../components/SingleBox'
import SingleHourBox from '../components/SingleHourBox'
import mainContext from '../context/mainContext'
import Modal from "../components/Modal"

const ReservationPage = () => {
    const {socket, getData, adminOnline} = useContext(mainContext) 
    const [getClicked, setClicked] = useState(null)
    const [showreserve, setShowReserve] = useState(false)
    const [getHour, setHour] = useState(0)
    const [openModal, setOpenModal] = useState(false)


        async function clickOnDay(day){
            setClicked(day)  
            // await socket.emit("gethours", day) 
            setShowReserve(false)
        }

        useEffect(()=> {
            socket.on("singleday", (day) => {
                setClicked(day)  
          }) 
        })





        function enableButton(x){

          if((!x.reservedBy && !adminOnline) || (x.reservedBy && adminOnline)){
            setShowReserve(true)
            setHour(x)

          } else {
            setShowReserve(false)
          }
        }

        async function changeReservation(){
          const reservationData= {
            type: "reserve",
            reservedBy: socket.id,
            day: getClicked.day,
            hour: getHour.hour
          }
          if(!adminOnline){
            await socket.emit("updatereservations", reservationData)
            await socket.emit("gethours", getClicked) 

          } else {

            setOpenModal(true)
          }


        }


  return (
    <div className="reservations">
      <div className="resdaycont">
    {getData && getData.map((x,i) => <SingleBox singleday={x} key={i} clickOnDay={() => clickOnDay(x)}/>)}
      </div>
      <div className="reshourcont" > 
      <div className="upper">
        {getClicked &&  <h3>Day: {getClicked.day}</h3> }
         <label>
          Select more hours:
        <select 
        // onChange={(event) => changeType(event.target.value)}
        // value={currentType}
      >

        <option value="empty"></option>
        <option value="regular">Regular</option>
        <option value="admin">Admin</option>

      </select>
      </label>
      </div>

      {getClicked && getClicked.hours.map((x,i) => <SingleHourBox hour={x} key={i} setShowReserve={setShowReserve} enableButton={() => enableButton(x)}/>)}
      {showreserve && <button onClick={changeReservation} >
        {adminOnline ? "Cancel reservation" : "Reserve"}
        </button>}

      </div>
      <Modal openModal={openModal} setOpenModal={setOpenModal} getClicked={getClicked} getHour={getHour}/>
    </div>
  )
}

export default ReservationPage
