import React, {useContext, useRef, useState} from 'react'
import mainContext from '../context/mainContext'

const Modal = ({openModal, getClicked, getHour, setOpenModal, useEffect}) => {

    const reasonRef = useRef()
    const {socket} = useContext(mainContext) 
    const [cancelled, setCancelled] = useState(false)

    async function cancelReservation(){
        const cancelationData = {
            type: "cancel",
            message: reasonRef.current.value,
            day: getClicked.day,
            hour: getHour.hour,
            reservedBy: getHour.reservedBy
        }
        await socket.emit("updatereservations", cancelationData)

        setCancelled(true)

    }

    function closeModal (){
      setOpenModal(false)
      setCancelled(false)
    }




  return (
    <div style={{display: openModal? "flex" : "none"}} className="modal">


  <div className="modal-content">
    <span onClick={closeModal}className="close">&times;</span>
    {cancelled ? (
      <div>Appointment has been successfully cancelled!</div>
    )
    : 
    (
      <div className="reasoncont">
    <h3>Please enter reason for canceling this appointment</h3>
    <input ref={reasonRef} type="text" placeholder="reason" />
    <button onClick={cancelReservation}>Send message and cancel appt</button>
    </div>
    )
  }


  </div>

</div>
  )
}

export default Modal
