import {React, useEffect, useState} from 'react'
import {useSelector, useDispatch} from "react-redux"
import SingleItem from '../components/SingleItem'
import { Container, Row} from 'react-bootstrap'

import {useNavigate} from "react-router-dom"
import data from "../assets/furniture"



function ShopPage({setsingleitem}) {

  const [items, setItems] = useState([])

  const nav = useNavigate()

useEffect(() => {

  setItems(data)

    // fetch("https://fakestoreapi.com/products")
    // .then(response => response.json())
    //     // 4. Setting *dogImage* to the image url that we received from the response above
    // .then(data => setItems(data))
  },[])


  console.log(items)

function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}





  return (

    <Container className='d-flex justify-content-center align-items-center m-auto py-5'>
       <Row xs={1} md={2} lg={4} className=" bg-succes" >  
          {items.map((x, i) => <SingleItem  item={x} key={i} openItem={() => openItem(x.id, x)}  /> )}
       </Row>
    </Container>
  )
}

export default ShopPage
