import React, {useState} from 'react'
import { useSelector } from 'react-redux'
import { useNavigate } from 'react-router'

const Toolbar = () => {
const quantity= useSelector(state => state.chart.value.quantity)
const totalprice = useSelector(state => state.chart.value.totalPrice)
console.log(`quantity: ${quantity}`)
const [openCart, setOpenCart] = useState(false)
const nav = useNavigate()

function openthiscart(){
nav("/cart")
setOpenCart(true)
}    

function goback(){
nav("/") 
setOpenCart(false)
}

  return (
    <div className="toolbar">
        <p>Cart items: {quantity}</p>
        <p>Total Price: {totalprice.toFixed(2)}</p>

        <button style={{display: openCart? "none" : "flex"}} onClick={openthiscart}>Open Cart</button>
        <button style={{display: openCart? "flex" : "none"}}  onClick={goback}>Go back to shop</button>
    </div>
  )
}

export default Toolbar
