import React, {useContext, useEffect, useState} from 'react'
import ReserveinMsg from '../components/ReserveinMsg'
import mainContext from '../context/mainContext'

const MessagesPage = () => {

    const [btn, setBtn] = useState(false)
    const [otherHour, setOtherHour] = useState([])
    const [successmsg, setsuccessmsg] = useState(null)

    const {socket, getmsg, freehours} = useContext(mainContext) 
    console.log(freehours)
    function showBtn(x){
            setBtn(true)
            setOtherHour([...otherHour, x.hour])
            console.log(otherHour)
        }

    async function reservation(){
          const reservationData= {
            type: "reserve",
            reservedBy: socket.id,
            day: freehours.day,
            hours: otherHour
          }

          console.log(reservationData)

            await socket.emit("updatereservations", reservationData)
            await socket.emit("deletemsg", socket.id)
            setsuccessmsg(`You have now booked an appointment on Day: ${freehours.day} Hour: ${otherHour.join(", ")}`)
          }






  return (
    <div className="msgcont">
        {getmsg && getmsg.map((x,i) => (
            <div className="msg">
                <div>
                <p>From: Admin</p>
                <p> Message:{x}</p>
                </div>
                <div className="reservehourmsg">
                    <h3>You can choose appt from following hours:</h3>

                      {freehours && freehours.hours.map((x, i) =>  <ReserveinMsg hour={x} key={i} showBtn={() => showBtn(x)} otherHour={otherHour}/>)}


                     <button style={{display: btn? "flex" : "none"}} onClick={reservation}>Reserve</button>
                </div>


            </div>

        ))}
        {successmsg && <h3>{successmsg}</h3>}
    </div>
  )
}

export default MessagesPage
