import {React, useEffect, useState} from 'react'
import {useSelector, useDispatch} from "react-redux"
import SingleItem from '../components/SingleItem'
import { Container, Row, Button, Dropdown} from 'react-bootstrap'

import {useNavigate} from "react-router-dom"
import data from "../assets/furniture"
import FilterItems from '../components/FilterItems'




function ShopPage({setsingleitem, items, setItems}) {

  const [show, setShow] = useState(false);
  const [filteredProducts, setFlteredProducts] = useState([])

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);   

  const nav = useNavigate()





function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}

const sortHightoLow = () => {
  if(filteredProducts.length > 0){
     const newArr = [...filteredProducts]
  let sorted = newArr.sort((x, y) => x.price - y.price)
  console.log(sorted)
  setFlteredProducts(sorted) 
  } else {
    const newArr = [...items]
  let sorted = newArr.sort((x, y) => x.price - y.price)
  console.log(sorted)
  setItems(sorted)
  }

}

const sortLowtoHigh = () => {
  if(filteredProducts.length > 0){
    const newArr = [...filteredProducts]
  let sorted = newArr.sort((x, y) => y.price - x.price)
  setFlteredProducts(sorted)
  } else {
    const newArr = [...items]
  let sorted = newArr.sort((x, y) => y.price - x.price)
  console.log(sorted)
  setItems(sorted)
  }

}




  return (

    <Container className='d-flex flex-column m-auto py-5'>
      <Container className='d-flex gap-2'>
        <Button className='w-25' variant="secondary" onClick={handleShow}>
        Filter products
      </Button >

      <Dropdown>
      <Dropdown.Toggle variant="secondary" id="dropdown-basic">
        Sort by:
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item onClick={sortHightoLow} href="#/action-1">Price low to high</Dropdown.Item>
        <Dropdown.Item onClick={sortLowtoHigh} href="#/action-2">Price high to low</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>

      </Container>

       <Container className='d-flex justify-content-center align-items-center m-auto py-5'>
        {filteredProducts.length > 0 ? (
            <Row xs={1} md={2} lg={4} className=" bg-succes" >  
          {filteredProducts.map((x, i) => <SingleItem  item={x} key={i} openItem={() => openItem(x.id, x)}  /> )}
       </Row>
       )
      :
      (
         <Row xs={1} md={2} lg={4} className=" bg-succes" >  
          {items.map((x, i) => <SingleItem  item={x} key={i} openItem={() => openItem(x.id, x)}  /> )}
       </Row> 
      )}

       </Container>

       <FilterItems handleClose={handleClose} show={show} items={items} setItems={setItems} setFlteredProducts={setFlteredProducts}/>
    </Container>
  )
}

export default ShopPage
