import data from "./furniture"

let colorList = []

module.exports = {



    getColorList: () => {

        const newarrr = [...data]

        newarrr.map(x => {
        const singlecolor = {
            color: x.color,
            quantity: 1
        }
        const colorExists = colorList.find(item => item.color === x.color)
        if(colorExists){
        colorExists.quantity++
        } else {
        colorList.push(singlecolor)
        }

    }) 
    },
}
