import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import SingleItemCart from '../components/SingleItemCart'
import { deletefromChart } from '../features/chart'

const ShoppingCartPage = () => {
const items = useSelector(state => state.chart.value.chartItems)  
const dispatch = useDispatch() 
console.log(items)

function deleteItem(index){

    const deleteditem = {
        item: items[index],
        array: [...items].filter((x, i) => i !== index)
    }



dispatch(deletefromChart(deleteditem))

}

  return (
    <div>
        {items.map((x, i ) => <SingleItemCart item={x} deleteItem={() => deleteItem(i)} />)}
    </div>
  )
}

export default ShoppingCartPage
