import React, {useState, useContext, useEffect} from 'react'
import EnemyBox from '../components/EnemyBox'
import SingleBox from '../components/SingleBox'
import mainContext from '../context/mainContext'


const GamePage = () => {


const {socket, playerID, nextTurn, logedinUser} = useContext(mainContext) 
const [showBtn, setShowBtn] = useState(false)
const [firstMove, setFirstMove] = useState(true)


const makeDamage = async () => {
  const randomDamage = Math.floor(Math.random() * (10 - 0) + 0)
  await  socket.emit("reduce_points", randomDamage) 
  await  socket.emit("who_should_go", socket.id) 
  setFirstMove(false)
}

console.log(nextTurn)

useEffect(()=> {

if(nextTurn){
  if(nextTurn === socket.id){
  setShowBtn(true)
} else {
  setShowBtn(false)
}
} else {

  console.log("something")
}


},[nextTurn])


const buyPotion  = async  () => {
    const randomHPpoints = Math.floor(Math.random() * (25 - 0) + 0)
    await  socket.emit("use_health_potion", randomHPpoints) 
}



  return (
    <div className='gameContainer'>
      <SingleBox/>
      <div className='center'>
          { (playerID === socket.id && firstMove) || showBtn  ? 
      (
         <button onClick={makeDamage}>attack</button>
      ) 
      :
      "Enemy's turn"
        }

      {logedinUser && logedinUser.points < 50 ? (
        <button onClick={buyPotion}>buy potion (price: 25)</button>
      )
      : 
      ""

       }  


      </div>

      <EnemyBox/>

    </div>
  )
}

export default GamePage
