import {React, useEffect, useState} from 'react'
import {useSelector, useDispatch} from "react-redux"
import SingleItem from '../components/SingleItem'
import { Container, Row, Button, Dropdown} from 'react-bootstrap'

import {useNavigate} from "react-router-dom"
import data from "../assets/furniture"
import FilterItems from '../components/FilterItems'



function ShopPage({setsingleitem, items}) {

  const [show, setShow] = useState(false);

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);   

  const nav = useNavigate()

  // useEffect(() => {

  // setItems(data)
  // console.log(items)

  // },[])





function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}





  return (

    <Container className='d-flex flex-column m-auto py-5'>
      <Container className='d-flex gap-2'>
        <Button className='w-25' variant="secondary" onClick={handleShow}>
        Filter products
      </Button >

      <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Dropdown Button
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>

      </Container>

       <Container className='d-flex justify-content-center align-items-center m-auto py-5'>
          <Row xs={1} md={2} lg={4} className=" bg-succes" >  
          {items.map((x, i) => <SingleItem  item={x} key={i} openItem={() => openItem(x.id, x)}  /> )}
       </Row>
       </Container>

       <FilterItems handleClose={handleClose} show={show}/>
    </Container>
  )
}

export default ShopPage
