import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Offcanvas from 'react-bootstrap/Offcanvas';

const FilterItems = ({handleClose, show, items, setItems}) => {

  const [colors, setColors] = useState([])
  const [furnTypes, setFurnTypes] = useState([])
  const [showsofas, setShowSofas] = useState()
  const [showarmchairs, setShowArmchairs] = useState()
  const [showpoufs, setShowPoufs] = useState()

//shows types and quantity of colors
  useEffect(()=> {
    let colorarr = []
    const newarrr = [...items]

    newarrr.map(x => {
      const singlecolor = {
        color: x.color,
        quantity: 1
      }
    const colorExists = colorarr.find(item => item.color === x.color)
    if(colorExists){
      colorExists.quantity++
    } else {
      colorarr.push(singlecolor)
    }

  })
      setColors(colorarr)
  },[])

//shows types and quantity of categories
  useEffect(()=> {
    let furnArr = []
    const newarrr2 = [...items]

    newarrr2.map(x => {
      const singleType = {
        type: x.category,
        quantity: 1
      }
    const typeExists = furnArr.find(item => item.type === x.category)
    if(typeExists){
      typeExists.quantity++
    } else {
      furnArr.push(singleType)
    }

  })
      setFurnTypes(furnArr)
  },[])

let filterArr = [...items]

const showCategory = (cat) => { 
  console.log(cat)
  if(cat === "poufs"){
    filterArr.filter(x => x.category === "poufs")
    console.log(filterArr)
  } else {
    return
  }

  // setItems(filterArr)


  }; 



  return (
     <>


      <Offcanvas show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Filter</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body className='d-flex flex-column'>

            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by category:</p>

                {furnTypes && furnTypes.map((x, i) => (
                    <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" onChange={() => showCategory(x.type)} />
                      {x.type} ({x.quantity})
                    </label>
                )

                )}
            </div>
            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by price:</p>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      0 - 500.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      500.00 - 800.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      800.00 - 1100.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      1100.00 - 1500.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      1500.00 - 2000.00 €
                </label>

            </div>
            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by color:</p>
                {colors && colors.map((x, i) => (
                    <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      {x.color} ({x.quantity})
                    </label>
                )

                )}

            </div>
          <div>
              <p>Show products with discount only:</p>
           <label>
                <input type="checkbox" />
            Discount
             </label>
          </div>

          <Button>Reset filter</Button>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  )
}

export default FilterItems
