import React, {useState, useEffect} from 'react'
import SingleBox from '../components/SingleBox'

const GamePage = () => {


    const arr = []
    const [gameBoxes, setGameBoxes] = useState([])
    const [flower, setFlower] = useState("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTx1J05nPOLGNQn10QnkqMHCTFqcr7Gl8jaAQ&usqp=CAU")
    const [bullets, setBullets] = useState([{position: 0}])
    const [zombie, setZombie] = useState({
        image: "https://img.freepik.com/free-photo/brain-eating-zombie-looking-terrifying_482257-48164.jpg?w=826&t=st=1662376584~exp=1662377184~hmac=43551088548387dbd0b4a556c71cddb831f2721e2c049302db7aea7574573790",
        position: 15
    })
    const [startShooting, setStartShooting] = useState(false)
    const [getmoveZombie, setMoveZombie] = useState(false)
    const [showBtn, setShowBtn] = useState(false)
    const [zombieLives, setZombieLives] = useState(100)

          useEffect(()=> {
             for(let i = 1; i < 15; i++){
            let box = {
                position: i,
                taken: ""

            }
             arr.push(box)  
             setGameBoxes(arr)  
          }
          },[])


   useEffect(() => {
    if(getmoveZombie){
        const timeout = setTimeout(() => {
            const currentPos = zombie.position
            const newPosition = {
            image: "https://img.freepik.com/free-photo/brain-eating-zombie-looking-terrifying_482257-48164.jpg?w=826&t=st=1662376584~exp=1662377184~hmac=43551088548387dbd0b4a556c71cddb831f2721e2c049302db7aea7574573790",
            position: currentPos - 1

                }
                setZombie(newPosition)
            },2000)
            if(zombie.position === 11) setShowBtn(true)

            if(zombie.position === 1){
                zombie.position = 15
                return () => clearTimeout(timeout)
            }
    }
    else return

   },[getmoveZombie, zombie])

    function makeashoot(){
        setStartShooting(true)
        setShowBtn(false)
        setTimeout(()=> {
           setShowBtn(true)

        },[1000])

         const newbullet  = {position: 0}
         setBullets([...bullets, newbullet])

   }


   useEffect(()=> {

        if(startShooting){

            const timeout = setTimeout(() => {
               const newbularr = [...bullets] 


             newbularr.map(x => {
                let currentPos = x.position
                    x.position = currentPos + 1
             })   

                setBullets(newbularr)
            },1000)

            const findIfMatch = bullets.find(x => x.position === zombie.position)
            if(findIfMatch){
                setZombieLives(zombieLives - 25)
                const newbularr = [...bullets] 
                newbularr.filter(z => z.position !== findIfMatch.position)
                console.log(zombie)
                setBullets(newbularr)
            }
                if(findIfMatch){

                return () => clearTimeout(timeout)
            } 
    }
   },[startShooting, bullets])







  return (
    <div className='gameContainer'>
        <h2> Zombie Lives: {zombieLives}</h2>
        <div className='gameRoad'>
            <div className='floweBox'><img className='boximage' src={flower} alt="" /></div>
        {gameBoxes.map((x, i) => <SingleBox  bullets={bullets} zombie={zombie} box={x} key={i} position={x.position} /> )}
        </div>
        <button onClick={() => setMoveZombie(true)}>start game</button>
        {showBtn && <button onClick={makeashoot}>Shoot</button>}


    </div>
  )
}

export default GamePage
