import React, {useRef, useContext, useEffect} from 'react'
import mainContext from '../context/mainContext'
import { useNavigate } from 'react-router'

const LoginPage = () => {

    const userref =useRef()
    const {socket, setLogedinUsers, logedinUsers} = useContext(mainContext) 
    const nav = useNavigate()

const connectUser = async () => {
    const user = userref.current.value

    socket.emit("connect_user", user)
                nav("/game")

}

useEffect(()=> {
        socket.on("user_connected", (data) => {
            console.log(data)
            setLogedinUsers([...logedinUsers,  data])
        })



    },[socket])


  return (
    <div className='homepage'>
        <input ref={userref} type="text" placeholder='user name' />
        <button onClick={connectUser}>Start game</button>
    </div>
  )
}

export default LoginPage
