import {React, useEffect, useState} from 'react'
import {useSelector, useDispatch} from "react-redux"
import {Container, Button, Row, Col, Card} from 'react-bootstrap'

import {useNavigate} from "react-router-dom"
import SingleItem from '../components/SingleItem'
import {FaArrowRight} from "react-icons/fa"


function HomePage({items, setsingleitem}) {

  const [headerItems, setHeaderItems] = useState([])
  const nav = useNavigate()


useEffect(() => {

  let newArr = [...items]

  const biggestDiscounts = newArr.filter(x => x.discount === true).sort((a, b) => 
  (b.withoutDiscount - b.price) - (a.withoutDiscount - a.price)).splice(0, 3)

  setHeaderItems(biggestDiscounts)
  console.log(biggestDiscounts)
},[])

const openAll = () => {
  nav("/shop")
}

function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}



  return (
   <Container >

    <Container className='d-flex flex-column py-4 my-5'>
      <h4 className='text-center mb-4 fw-bold'>Check out our hottest deals!</h4>
       <Row xs={1} md={2} lg={4} className=" bg-succes" >  
      {headerItems && headerItems.map((x, i ) => <SingleItem key={i} item={x} openItem={() => openItem(x.id, x)}/>)}
      <Col onClick={openAll} >
        <Card className=' flex-center flex-column bg-light cusstomBoxStyle customCardAllProducts mb-4'>
          <Card.Title className='text-center fw-bold'>
            <h3>SEE ALL</h3>
            <FaArrowRight className='fs-2 mt-3'/>
      </Card.Title>
        </Card>
      </Col>
      </Row>
      </Container>

    </Container>

  )
}

export default HomePage
