import React, {useState, useEffect} from 'react'
import SingleBox from '../components/SingleBox'

const GamePage = () => {


    const arr = []
    const [gameBoxes, setGameBoxes] = useState([])
    const [flower, setFlower] = useState("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTx1J05nPOLGNQn10QnkqMHCTFqcr7Gl8jaAQ&usqp=CAU")

          useEffect(()=> {
             for(let i = 1; i < 10; i++){
            let box = {
                position: i,
                taken: ""

            }
             arr.push(box)  
             setGameBoxes(arr)  
          }
          },[])


    const startGame = () => {

        console.log(gameBoxes)
        const playerPosition = {
            position: 0,
            taken: flower
        }
        gameBoxes.unshift(playerPosition)
}

  return (
    <div className='gameContainer'>
        <div className='gameRoad'>
        {gameBoxes.map((x, i) => <SingleBox box={x} key={i} /> )}
        </div>
        <button onClick={startGame}>start game</button>
        <button>Shoot</button>
        <button> move zombie </button>
    </div>
  )
}

export default GamePage
