import React, {useRef, useState} from 'react'

const RegisterPage = () => {

const emailRef = useRef()
const passOneRef = useRef()
const passTwoRef = useRef()
const [currentType, setCurrentType] = useState(null)
const [message, setMessage] = useState("")

function changeType(type){
 setCurrentType(type)
}

function registerUser(){

  if(currentType !== "empty" && emailRef.current.value !== "" && passOneRef.current.value !== "" && passTwoRef.current.value !== "empty" && passOneRef.current.value === passTwoRef.current.value){
    const newUser = {
        usertype: currentType,
        useremail : emailRef.current.value,
        userpass: passOneRef.current.value,
        passTwo: passTwoRef.current.value
    }



const options = {
        method: "POST",
        headers: {
            "content-type": "application/json"
        },
        body: JSON.stringify(newUser)

    }

        fetch("http://localhost:4000/regUser", options)
      .then(res => res.json())
      .then((data) => {

        if(!data.error){
          setMessage("Registered successfully")
        }


  })  
  } else return

}

  return (
    <div className="register">
        <h3>Register</h3>
        <label>
          Select user type:
        <select 
        onChange={(event) => changeType(event.target.value)}
        value={currentType}
      >

        <option value="empty">-</option>
        <option value="regular">Regular</option>
        <option value="admin">Admin</option>

      </select>
      </label>

        <input type="text" ref={emailRef}  placeholder="email"/>
        <input type="text" ref={passOneRef} placeholder="password"/>
        <input type="text"  ref={passTwoRef} placeholder="repeat password"/>
        <button onClick={registerUser}>Register</button>
        {message && <p>{message}</p>}
    </div>
  )
}

export default RegisterPage
