import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { useSelector, useDispatch } from 'react-redux'

const FilterItems = ({handleClose, show}) => {


  const [checkedFurn, setCheckedFurn] = useState([])
  const filteredItems = useSelector(state => state.items.value.filteredProducts)
  const colors = useSelector(state => state.items.value.colors)
  const categories = useSelector(state => state.items.value.categories)



let filterArr = [...filteredItems]

const showCategory = (categ) => { 

  const currentIndex = checkedFurn.indexOf(categ)
  const newCheckedFurn = [...checkedFurn]

  if(currentIndex === -1){
    newCheckedFurn.push(categ)
  }
  else {
    newCheckedFurn.splice(currentIndex, 1)
  }

  setCheckedFurn(newCheckedFurn)


  //   let filtered = filterArr.filter(x => x.category === categ)

  // setFlteredProducts(filtered)
  // console.log(items)

  }; 

console.log(checkedFurn)

  return (
     <>


      <Offcanvas show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Filter</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body className='d-flex flex-column'>

            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by category:</p>

                {categories && categories.map((x, i) => (
                    <label className='d-flex gap-2 px-2'>
                    <input 
                    type="checkbox" 
                    checked={checkedFurn.indexOf(x.type) === -1 ? false : true}  
                    onChange={() => showCategory(x.type)} />
                      {x.type} ({x.quantity})
                    </label>
                )

                )}
            </div>
            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by price:</p>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      0 - 500.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      500.00 - 800.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      800.00 - 1100.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      1100.00 - 1500.00 €
                </label>
                <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      1500.00 - 2000.00 €
                </label>

            </div>
            <div className='d-flex flex-column gap-1 my-4'>
                <p>Filter by color:</p>
                {colors && colors.map((x, i) => (
                    <label className='d-flex gap-2 px-2'>
                    <input type="checkbox" />
                      {x.color} ({x.quantity})
                    </label>
                )

                )}

            </div>
          <div>
              <p>Show products with discount only:</p>
           <label>
                <input type="checkbox" />
            Discount
             </label>
          </div>

          <Button>Reset filter</Button>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  )
}

export default FilterItems
