import React from 'react'

const SingleHourBox = ({hour, enableButton, multipleHours, index}) => {
  const findIfSelected = multipleHours.find(x => x.hour === hour.hour)
  const bgStyle = {backgroundColor: hour.reservedBy !== "" ? "#990000" : "white"}
  const borderStyle = {border: findIfSelected? "2px solid #990000" : "none"}
  console.log(multipleHours)
  console.log(hour.hour)
  console.log(multipleHours[0].hour)



  return (
    <div 
    onClick={enableButton}
    style={{...bgStyle, ...borderStyle}}

    >Hour: { hour.hour}</div>
  )
}

export default SingleHourBox
