    const newarr = [...gameBoxes]

    const zombieInRoad = {
        position: newarr.length - 1,
        taken: {zombie}

    }
        newarr.push(zombieInRoad)
        console.log(zombieInRoad)
        console.log(newarr)
        setGameBoxes(newarr)
        // console.log(gameBoxes)
}

  return (
    <div className='gameContainer'>
        <div className='gameRoad'>
            <div className='floweBox'><img className='boximage' src={flower} alt="" /></div>
        {gameBoxes.map((x, i) => <SingleBox box={x} key={i} /> )}
        </div>
        <button onClick={startGame}>start game</button>
        <button>Shoot</button>
        <button> move zombie </button>
    </div>
  )
}

export default GamePage
