import React, {useState, useEffect} from 'react'
import SingleBox from '../components/SingleBox'

const GamePage = () => {


    const arr = []
    const [gameBoxes, setGameBoxes] = useState([])
    const [flower, setFlower] = useState("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTx1J05nPOLGNQn10QnkqMHCTFqcr7Gl8jaAQ&usqp=CAU")
    const [zombie, setZombie] = useState("https://img.freepik.com/free-photo/brain-eating-zombie-looking-terrifying_482257-48164.jpg?w=826&t=st=1662376584~exp=1662377184~hmac=43551088548387dbd0b4a556c71cddb831f2721e2c049302db7aea7574573790")
    const [startShooting, setStartShooting] = useState(false)

          useEffect(()=> {
             for(let i = 1; i < 15; i++){
            let box = {
                position: i,
                taken: ""

            }
             arr.push(box)  
             setGameBoxes(arr)  
          }
          },[])


    const startGame = () => {   
    const newarr = [...gameBoxes]
    const zombieInRoad = {
        position: newarr.length - 1,
        taken: zombie

    }
        newarr.push(zombieInRoad)
        setGameBoxes(newarr)
}

   const moveZombie = () => {
    const newarr = [...gameBoxes]
        const currentPosition = newarr.find(x => x.taken === zombie)
        console.log(currentPosition)
        let nextPosition = currentPosition.position - 3
        const findNewPosition = gameBoxes.find(x => x.position === nextPosition)
        currentPosition.taken = ""
        findNewPosition.taken = zombie
        setGameBoxes(newarr)

   }

  return (
    <div className='gameContainer'>
        <div className='gameRoad'>
            <div className='floweBox'><img className='boximage' src={flower} alt="" /></div>
        {gameBoxes.map((x, i) => <SingleBox box={x} key={i} position={x.position} /> )}
        </div>
        <button onClick={startGame}>start game</button>
        <button>Shoot</button>
        <button onClick={moveZombie}> move zombie </button>
    </div>
  )
}

export default GamePage
