import mainContext from '../context/mainContext'


const GamePage = () => {

const playerImages = [
        "https://images.pexels.com/photos/7594635/pexels-photo-7594635.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
        "https://images.pexels.com/photos/3621094/pexels-photo-3621094.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
    ]
const {socket, setLogedinUser, logedinUser, enemy} = useContext(mainContext) 
const [playersTurn, setPlayersTurn] = useState(true)


const makeDamage = async () => {
  const randomDamage = Math.floor(Math.random() * (10 - 0) + 0)
  console.log(randomDamage)
  console.log(socket.id)
  await  socket.emit("reduce_points", randomDamage)
}





  return (
    <div className='gameContainer'>
      <SingleBox image={playerImages[0]}/>
      {playersTurn &&  <button onClick={makeDamage}>attack</button> }


      <EnemyBox/>

    </div>
  )
}

export default GamePage
