import React, {useContext, useEffect, useState} from 'react'
import ReserveinMsg from '../components/ReserveinMsg'
import mainContext from '../context/mainContext'

const MessagesPage = () => {

    const [btn, setBtn] = useState(false)

    const {socket, getmsg, frehours} = useContext(mainContext) 

    function showBtn(x){

          if((!x.reservedBy && !adminOnline) || (x.reservedBy && adminOnline)){
            setBtn(true)
            setHour(x)

          } else {
            setShowReserve(false)
          }
        }

        async function reservation(){
          const reservationData= {
            type: "reserve",
            reservedBy: socket.id,
            day: getClicked.day,
            hour: getHour.hour
          }
          if(!adminOnline){
            await socket.emit("updatereservations", reservationData)
            await socket.emit("gethours", getClicked) 

          } 


        }



  return (
    <div className="msgcont">
        {getmsg && getmsg.map((x,i) => (
            <div className="msg">
                <div>
                <p>From: Admin</p>
                <p> Message:{x}</p>
                </div>
                <div className="reservehourmsg">
                    Please choose your appt from following hours:
                     {frehours && frehours.map((x, i) =>  <ReserveinMsg hour={x} key={i} showBtn={() => showBtn(x)}/>)}
                     <button style={{display: btn? "flex" : "none"}} onClick={reservation}>Reserve</button>
                </div>


            </div>

        ))}
    </div>
  )
}

export default MessagesPage
