import { useSelector, useDispatch } from 'react-redux'
import { deletefromChart } from '../features/chart'


const OtherItems = ({items, otherItem, setsingleitem}) => {

    const [random, setRandom] = useState([])
    const dispatch = useDispatch() 
    const nav = useNavigate()




 function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}



  return (
   <Container>
    <Row xs={2} md={3} lg={5} className=" bg-succes">

    {random && random.map((x,i) => (
        <SingleItem item={x} openItem={() => openItem(x.id, x)}/>
    ))}
    </Row>
   </Container>
  )
}

export default OtherItems
