import React, { useEffect, useState } from 'react'
import {Container, Button, Card} from 'react-bootstrap'

const OtherItems = ({items, otherItem}) => {

    const [random, setRandom] = useState([])

 useEffect(() => {
    const newArr = [...items]
    const randomItems = newArr.filter(x => x.category === otherItem).sort((a, b) => 0.5 - Math.random()).splice(0, 4)

    setRandom(randomItems)
 },[])



  return (
    <div>OtherItems</div>
  )
}

export default OtherItems
