import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { addToChart } from '../features/chart'
import { useNavigate } from 'react-router'
import {Container, Button, Card} from 'react-bootstrap'
import OtherItems from '../components/OtherItems'


const SingleProductPage = ({getsingleitem, setsingleitem}) => {
const nav = useNavigate()
const dispatch = useDispatch()

    function addItemtoChart(){
const newaddeditem = {
    id: getsingleitem.id,
    image: getsingleitem.image,
    title: getsingleitem.title,
    price: getsingleitem.price,
    totalPrice: getsingleitem.price,
    quantity: 1

}

        dispatch(addToChart(newaddeditem))
    }

 const items = useSelector(state => state.chart.value.chartItems)   


    function gobacktopreviouspage(){
        nav("/shop")
        setsingleitem({})
    }
  //  { name: "Pouf SPA",
  //       category: "poufs",
  //      image: "https://cdn.shopify.com/s/files/1/0056/0912/8000/products/MONOpufasb_1800x1800.jpg?v=1641483397",
  //      itemsleft: 8,
  //      price: 359.00,
  //      discount: true,
  //      withoutDiscount: 429.00,
  //      color: "grey",
  //      id: 25
  //   },



  return (
<div>

    <Container className='w-100 h-100 py-5 d-flex flex-column'>
      <Container className='d-flex h-100 mb-5'>
          <Container className='w-50'>
          <img className='w-100' src={getsingleitem.image} alt="" />
        </Container>
        <Container className='w-50 h-100 d-flex flex-column py-4 my-auto'>
          <h3>{getsingleitem.name}</h3>
          <p>{getsingleitem.description}</p>
          <p>Left items:  {getsingleitem.itemsleft}</p>
          <div className='d-flex gap-3' style={{height: "30px"}}> Color: 
            <div className='rounded-circle' style={{backgroundColor: getsingleitem.color, width: "30px", height: "30px"}}></div>
          </div>
          {getsingleitem.discount === true ? (
             <div className='flex-center flex-column'>
            <div className='d-flex gap-2'>
               <p className='text-secondary fs-6'><s>{getsingleitem.withoutDiscount}€</s></p>
                <p className='text-danger fs-4 fw-bold'>{getsingleitem.price}€</p> 
            </div>

            <div className='bg-danger w-50 fs-6 text-center py-1 rounded mx-auto text-light'>Save {getsingleitem.withoutDiscount - getsingleitem.price} € !</div>
          </div> 
          )
          :
          (
             <p className='text-danger fs-4 fw-bold text-center my-4'>{getsingleitem.price}€</p>  
          )}


         <Container className='d-flex justify-content-center align-items-end my-4 gap-3'>
            <Button className='bg-secondary border-0' onClick={addItemtoChart}>ADD TO CART</Button>
            <Button className='bg-secondary border-0' onClick={gobacktopreviouspage}>GO BACK</Button>
        </Container>
        </Container>
      </Container>



    <Container>
        Other items you may like:
        <OtherItems otherItem={getsingleitem.category} />
    </Container>
    </Container>


</div>


  )
}

export default SingleProductPage
