const OtherItems = ({items, otherItem}) => {

    const [random, setRandom] = useState([])

 useEffect(() => {
    const newArr = [...items]
    const randomItems = newArr.filter(x => x.category === otherItem.category &&  x.id !== otherItem.id).sort((a, b) => 0.5 - Math.random()).splice(0, 5)

    setRandom(randomItems)
 },[])


 function openItem (id, item){
  nav("/product/" + id)
  setsingleitem(item)
}



  return (
   <Container>
    <Row xs={2} md={3} lg={5} className=" bg-succes">

    {random && random.map((x,i) => (
        <SingleItem item={x} openItem={() => openItem(x.id, x)}/>
    ))}
    </Row>
   </Container>
  )
}

export default OtherItems
