import './App.css';
import data from "./assets/furniture"
import {React, useState, useEffect} from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import ShopPage from './pages/ShopPage';
import SingleProductPage from './pages/SingleProductPage';
import ShoppingCartPage from './pages/ShoppingCartPage';
import HomePage from './pages/HomePage';
import Toolbar from './components/Toolbar';
import Header from './components/Header';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
const [getsingleitem, setsingleitem] = useState()

const [items, setItems] = useState([])



  useEffect(() => {

  setItems(data)
  console.log(items)

  },[])



  return (



<div className="App"> 
<BrowserRouter>
<Header/>
<Toolbar/>
<Routes>

 <Route path="/shop" element={<ShopPage items={items} setsingleitem={setsingleitem}/>}/>
 <Route path="/" element={<HomePage/>}/>
 <Route path="/product/:id" element={<SingleProductPage getsingleitem={getsingleitem} setsingleitem={setsingleitem}/>}/>
 <Route path="/cart" element={<ShoppingCartPage/>}/>


</Routes>

</BrowserRouter>




</div>







  );
}

export default App;
